jQuery( document ).ready(function( $ ) {
  // Function to decide which calendar to show

  function chooseCalendar() {
    if (location.pathname.indexOf('women') != -1) {
      return womenCalendars;
    } else if (location.pathname.indexOf('men') != -1) {
      return menCalendars;
    } else if (location.pathname.indexOf('touch') != -1) {
      return touchCalendars;
    } else if (location.pathname.indexOf('school') != -1) {
      return schoolCalendars
    } else {
      return allCalendars;
    }
  }

  // Club calendar
  var clubCalendar = {
    events: facebookEvents,
    color: "#fa6900"
  };

  // Men calendars
  var menCalendars = {
    googleCalendarId: 'c18un05f0m1o7kvi58nb863cdduotbdi@import.calendar.google.com',
    color: "#00994c"
  };

  // Touch calendars
  var touchCalendars = {
    googleCalendarId: 'rqvfi5889grtqnojljp600serk@group.calendar.google.com',	  
    color: "#ce0000"
  };

  // Women calendars
  var womenCalendars = {
    googleCalendarId: '8nj23co4raufc6eti6db8ujj7vb4ke3v@import.calendar.google.com',
    color: "purple"
  };

  // School calendars
  var schoolCalendars = {
    googleCalendarId: 'rugbyschool@brusselsceltic.com',
    color: "#3a87ad"
  };

  // All calendars
  var allCalendars = [clubCalendar, menCalendars, touchCalendars, womenCalendars, schoolCalendars];

  calendarChosen = chooseCalendar();

  $('#calendar').fullCalendar({
    // important!
    googleCalendarApiKey: 'AIzaSyD3jRcW77Y10CYpCXWLdC6WJrmCT5GXyH4',
    eventSources: calendarChosen,

    // Customisation fullCalendar
    firstDay: 1,
    eventRender: function(event, element) {
      event.start.timeZone = "Europe/Brussels";
      event.end.timeZone = "Europe/Brussels";
      var textToShow = " : " + event.start.format('HH:mm');
      /* Women calendars on Teamer have fake end times so excluded
      from end times formatting below */
      if (event.end && event.source.googleCalendarId != womenCalendars.googleCalendarId) {
	textToShow = textToShow.concat(' - ' + event.end.format('HH:mm'));
      }
      element.tooltip({
	title: event.title + textToShow,
	/* Setting container makes the tooltip display on top of other
	elements/overlapping divs */
	container: 'body'
      });
    },
    timezone: "Europe/Brussels",
    header: {
      left: "agendaWeek basicWeek month",
      center: "title",
      right: 'today prev,next',
    },
    views: {
      basic: {
	// options apply to basicWeek and basicDay views
      },
      agenda: {
	// options apply to agendaWeek and agendaDay views
      },
      week: {
	// options apply to basicWeek and agendaWeek views
      },
      day: {
	// options apply to basicDay and agendaDay views
      },
      month: {
	buttonText: "Month",
	timeFormat: "H:mm",
	displayEventEnd: false,
      },
      basicWeek: {
	buttonText: "Week",
	timeFormat: "H:mm",
	displayEventEnd: true,
      },
      agendaWeek: {
	buttonText: "Agenda",
	timeFormat: "H:mm",
	displayEventEnd: true,

      },
    },
  });

  $(".rotation").tooltip({
    title: "Rotation every 2 weeks"
  });
});
$(document).ready(function() {

  function shuffle(o) {
    for(var j, x, i = o.length; i; j = parseInt(Math.random() * i), x = o[--i], o[i] = o[j], o[j] = x);
    return o;
  };
  var originalPhotos = [
    "assets/img/banner-men.jpg",
    "assets/img/banner-men2.jpg",
    "assets/img/banner-ladies.jpg",
    "assets/img/banner-touch.jpg",
    "assets/img/banner-school.jpg",
    "assets/img/banner-school2.jpg"
  ];

  /* Pictures are shuffled on refresh, so sections do not complain one
  particular photo is always the first one to show on screen. Only 3
  are chosen at a time */
  function activation() {
    $('div#banner').backstretch(shuffle(originalPhotos).slice(0, 3), {duration: 2000, fade: 550});
  }

  function checkOnResize() {
    var status = $('div#banner').data("backstretch");
    if (checkSize() == "big" &&  status == undefined) {
      activation();
    } else if (checkSize() == "big") {
      $.noop();
    } else if (checkSize() == "small" && status == undefined) {
      $.noop();
    } else {
      activation();
    }
  }

  function switchStates() {
    if ($("i.fa", $('div#banner')).hasClass("fa-pause")) {
      $('div#banner').backstretch('pause');
      $("i.fa-pause").attr("class", "fa fa-play");
    } else {
      $('div#banner').backstretch('resume');
      $("i.fa-play").attr("class", "fa fa-pause");
    }
  }

  function checkSize(){
    if ($('#banner').width() < 768 ){
      return "small"
    } else {
      return "big"
    }
  }

  activation();
  /* Init stuff */
  /* if (checkSize() == "big") {
     activation();
     } */
  /* $(window).resize(checkOnResize); */
  /* Click on div to decide if pause or resume, navbar not included */
  $('div#banner > :not(.navbar)').click(switchStates);

});



